        <script>
            const checkout = () => {
                fetch('/{{company_id()}}/billing/subscribe', {
                    headers: {
                        'Content-Type': "application/json",
                        'X-CSRF-TOKEN': window.Laravel.csrfToken,
                        'Accept': "application/json"
                    },
                    method: "POST"
                })
                    .then(a => a.json())
                    .then(a => {
                        window
                            .Stripe('{{$stripeKey}}')
                            .redirectToCheckout({
                                sessionId: a.session_id,
                            })
                            .then(function (result) {
                                console.error('result', result);
                            });
                    })
            }
        </script>
